const myImage = new Image;
myImage.src = 'data:image/jpeg;base64,';


// dimension =  Math.floor(visualViewport.height / 3);
const width = x_factor * dimension;
const height = dimension * y_factor;


const canvas = document.getElementById('canvas1');
canvas.height = height;
canvas.width =  width;
const ctx = canvas.getContext('2d');


myImage.addEventListener('load', function (){
    ctx.drawImage(myImage, 0, 0, width, height);
    const scannedImage = ctx.getImageData(0, 0, width, height);
    ctx.clearRect(0, 0, width, height)
    
    const numOfParticles = 500;
    let particleArray = [];

    let mappedImage = [];

    for (let y = 0; y < height; y++ ) {
        let row = [];
        for (let x = 0; x < width; x++) {
            const f = 4 * (y * width + x);
            const red = scannedImage.data[f];
            const green = scannedImage.data[f + 1];
            const blue = scannedImage.data[f + 2];
            const brightness = getRelativeBrightness(red, green, blue);

            row.push({brightness, color: `rgb(${red},${green},${blue})`});
        }
        mappedImage.push(row)
    }


    function getRelativeBrightness(red, green, blue) {
        return Math.sqrt(
            (red * red) * 0.299 +
            (green * green) * 0.587 +
            (blue * blue) * 0.114
        )/255;
    }
    
    class Particle {
        constructor(){
            this.x = Math.random() * (width / 2.2) + ((1.2 * width)/ 2.2);
            this.y = Math.random() * height;
            // this.y = 0;
            this.underlyingPixel = mappedImage[Math.floor(this.y)][Math.floor(this.x)];
            this.velocity = Math.random() * 4 + 2.5;
            this.maxSpeed = 5;
            this.binary = Math.floor(Math.random() * 2);
            this.speed = (0 + this.underlyingPixel.brightness) * this.maxSpeed;
            this.maxSize = 8;
            this.size = 1 * this.maxSize;
            this.movement = this.velocity;
        }

        update () {
            this.movement = this.velocity + this.speed;
            this.y += this.movement;


            if ((this.y >= height)) {
                this.y = 0;
                this.x = Math.random() * (width / 2.2) + ((1.2 * width)/ 2.2);
            }


            this.underlyingPixel = mappedImage[Math.floor(this.y)][Math.floor(this.x)];
            this.speed = (0 + this.underlyingPixel.brightness) * this.maxSpeed;
            this.size = this.underlyingPixel.brightness * this.maxSize;


        }

        draw() {
            ctx.fillStyle = `rgb(120, ${this.underlyingPixel.brightness * 100 + 100}, 100)`;
            ctx.fillRect(this.x, this.y, this.size, this.size);

            ctx.fillStyle = 'black'
            ctx.font = `${this.size + 8}px Sans Serif`;
            ctx.fillText(this.binary, this.x, this.y + this.size)

        }
    }

    function inti() {
        for (let i = 0; i < numOfParticles; i++){
            particleArray.push(new Particle);
        }
    }

    inti();

    function animate() {

        ctx.globalAlpha = 0.02;
        ctx.fillStyle = 'black'
        ctx.fillRect(0, 0, width, height);

        for (let i = 0; i < numOfParticles; i++) {

            particleArray[i].update();
            ctx.globalAlpha = (particleArray[i].underlyingPixel.brightness) * 1;
            particleArray[i].draw();

        }

        requestAnimationFrame(animate)

    }
    
    requestAnimationFrame(animate)
});
